import React, { useState } from "react";

export default function TravelCard(props) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleAccordion = () => {
    setIsOpen(!isOpen);
  };


  const onDelete = (id) => {
    fetch(`http://localhost:3001/api/travels/${id}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (res.ok) {
          alert("ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
          window.location.reload();
        } else {
          alert("ÏÇ≠Ï†ú Ïã§Ìå®");
        }
      })
      .catch((err) => console.error("ÏÇ≠Ï†ú Ïã§Ìå®:", err));
  }




  return (
    <div className="card">
      <img 
        src={props.imageUrl} 
        alt="Ïó¨ÌñâÏßÄ" 
        className="card--image"
      />
      <div className="card--content">
        <div className="card--location">
          <span>{props.location}</span> üìç
          <a href={props.googleMapsUrl} target="_blank" rel="noopener noreferrer">
            ÏßÄÎèÑ Î≥¥Í∏∞
          </a>
        </div>

        {/* Ïó¨Í∏∞ titleÎßå ÌÅ¥Î¶≠Ìï† Ïàò ÏûàÍ≤å */}
        <h2 
          onClick={toggleAccordion}
          style={{ cursor: "pointer", color: "#0077cc" }}
        >
          {props.title}
        </h2>



        <p className="card--date">{props.startDate} ~ {props.endDate}</p>




        <button onClick={() => onDelete(props.id)}>ÏÇ≠Ï†ú</button>
        {/* ÏÇ≠Ï†ú ÎêòÎäîÍ±¥Í∞Ä? */}


        {isOpen && (
          <div className="card--detail">
            <p>{props.description}</p>
          </div>
        )}
      </div>
    </div>
  );
}
